package inc.sguard.android.com.sguardinc;import android.content.Context;import android.support.annotation.NonNull;import android.support.v7.widget.RecyclerView;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ImageView;import android.widget.RelativeLayout;import android.widget.TextView;import com.squareup.picasso.Picasso;import java.util.ArrayList;import inc.sguard.android.com.sguardinc.Model.Guard;import inc.sguard.android.com.sguardinc.Model.User;import inc.sguard.android.com.sguardinc.UserChat.ChatDetails;public class GuardCurrentOrderAdapter extends RecyclerView.Adapter<GuardCurrentOrderAdapter.MyViewHolder> {    Context context;    ArrayList<User> profiles;    public GuardCurrentOrderAdapter(Context c, ArrayList<User> p) {        context = c;        profiles = p;    }    @NonNull    @Override    public GuardCurrentOrderAdapter.MyViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {        return new GuardCurrentOrderAdapter.MyViewHolder(LayoutInflater.from(context).inflate(R.layout.user_list_row, parent, false));    }    @Override    public void onBindViewHolder(@NonNull GuardCurrentOrderAdapter.MyViewHolder holder, final int position) {        ChatDetails.chatname =  profiles.get(position).getName();        holder.name.setText("Name: " + profiles.get(position).getName());        holder.email.setText("Email: " + profiles.get(position).getEmail());        holder.dob.setText("DateOfBirth: " + profiles.get(position).getDateOfBirth());        holder.status.setText("UserName: " + profiles.get(position).getUsername());        if (profiles.get(position).getPhotoURL().equals("default")) {            holder.profilePic.setImageDrawable(context.getResources().getDrawable(R.drawable.ifour));        } else            Picasso.get().load(profiles.get(position).getPhotoURL()).into(holder.profilePic);    }    @Override    public int getItemCount() {        return profiles.size();    }    class MyViewHolder extends RecyclerView.ViewHolder {        TextView name, email, dob, status;        ImageView profilePic;        RelativeLayout main_layout;        public MyViewHolder(View itemView) {            super(itemView);            main_layout = (RelativeLayout) itemView.findViewById(R.id.main);            name = (TextView) itemView.findViewById(R.id.name);            email = (TextView) itemView.findViewById(R.id.email);            profilePic = (ImageView) itemView.findViewById(R.id.imageView);            dob = (TextView) itemView.findViewById(R.id.dob);            status = (TextView) itemView.findViewById(R.id.status);        }        public void onClick(final int position) {          /*  profilePic.setOnClickListener(new View.OnClickListener() {                @Override                public void onClick(View v) {                    Toast.makeText(context, profiles.get(position).getName() + " is clicked", Toast.LENGTH_SHORT).show();                }            });*/        }    }}